function searchPosts(inputValue){const currentOrigin=window.location.origin,outputDiv=document.querySelector(".resultSearch"),url=currentOrigin+"/Social/searchPost";""!==inputValue?fetch(url,{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:"string="+inputValue}).then(response=>response.text()).then(data=>{outputDiv.innerHTML=data,outputDiv.style.display="block"}):(outputDiv.innerHTML="",outputDiv.style.display="none")}document.addEventListener("DOMContentLoaded",()=>{const searchInput=document.querySelector("#search"),outputDiv=document.querySelector(".resultSearch");searchInput.addEventListener("keyup",(function(){searchPosts(this.value)})),searchInput.addEventListener("focus",(function(){outputDiv.style.display="block"}))});